    private void setStyle()
    {
        String a = "Monospaced";
        String b = "Courier new";
        
        int style = Font.PLAIN;
        if(pochylony.isSelected())
        {
         style += Font.ITALIC;    
        }
        if(pogrubiony.isSelected())
        {
         style += Font.BOLD;
        }
        if(font_1.isSelected())
        {
             etykieta.setFont(new Font(a, style, 25));
        }
        if(font_2.isSelected())
        {
            etykieta.setFont(new Font(b, style, 25));
        }
    }
